/// <summary>
/// Replace country region token in full primary address.
/// </summary>
public void postLoad()
{
    AxDataEntityView AxDataEntityView;

    AxDataEntityView = Microsoft.Dynamics.Ax.Xpp.MetadataSupport::GetDataEntityView(tableId2Name(this.TableId));

    if(AxDataEntityView)
    {
        this.DeepLinkURL =  AAXURLHelper::generateDeepLink( AxDataEntityView.FormRef,
                                                        MenuItemType::Display,
                                                        formDataSourceStr(CustTable, CustTable),
                                                        fieldStr(CustTable,AccountNum),
                                                        this.CustomerAccount);
    }

    next postLoad();

}
